import React, { useState, useRef } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Category from './Category';
import Home from './Home';
import Login from './Login';
import Register from './Register';  // Import Register page
import Layout from './Layout';
import NoPage from './NoPage';
import './style.css';

export default function App() {
  const [uname, unameSetter] = useState(null);
  const unameRef = useRef();
  const pwordRef = useRef();

  const handleLogout = () => {
    unameSetter(null);
  };

  const handleLogin = async () => {
    const user = {
      uname: unameRef.current.value,
      pword: pwordRef.current.value,
    };

    const parameters = {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(user),
    };

    const url = `http://localhost:5001/users/login`;

    try {
      const res = await fetch(url, parameters);

      if (!res.ok) {
        throw new Error('Failed to fetch from the server');
      }

      const json = await res.json();
      if (json.success) {
        console.log('Valid username and password');
        unameSetter(user.uname);
      } else {
        console.log('Invalid credentials');
      }
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  const handleRegister = async () => {
    const user = {
      uname: unameRef.current.value,
      pword: pwordRef.current.value,
    };

    const parameters = {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(user),
    };

    const url = `http://localhost:5001/users/register`; // Update to the register endpoint

    try {
      const res = await fetch(url, parameters);

      if (!res.ok) {
        throw new Error('Failed to fetch from the server');
      }

      const json = await res.json();
      if (json.success) {
        console.log('User registered successfully');
        unameSetter(user.uname);  // Automatically log in after registration
      } else {
        console.log('Registration failed');
      }
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Layout />}>
          <Route index element={<Home uname={uname} />} />
          <Route path="contacts" element={<Category />} />
          <Route path="*" element={<NoPage />} />
        </Route>
        <Route path="/login" element={<Login unameRef={unameRef} pwordRef={pwordRef} handleLogin={handleLogin} />} />
        <Route path="/register" element={<Register unameRef={unameRef} pwordRef={pwordRef} handleRegister={handleRegister} />} />
      </Routes>
    </BrowserRouter>
  );
}





