/*
import React, {useState, useRef} from "react";

import { BrowserRouter, Routes, Route } from "react-router-dom";

import Category from './Category.js';

import Home from './Home.js';

import Login from './Login.js';

import Layout from './Layout.js';

import NoPage from './NoPage.js';

import './style.css';

const user = ""

export default function App() {
const [uname, unameSetter] = useState(null);
const unameRef = useRef();
const pwordRef = useRef();

const handleLogout = () => {
  unameSetter(null);
}
const handleLogin = () => {
  let user = {};
  user.uname = useRef.current.value;
  user.pword = pwordRef.current.value;
}

let parameters = {
  method: "POST",
  headers: {
    "Accept": "application/json",
    "Content-Type": "application/json"
  },
  body: JSON.stringify(user)
}
console.log('user.uname called')
let url = `http://localhost:5001/users`;
fetch (url, parameters)
.then( res => res.json())
.then( json => {
  console.log(JSON.stringify(json))
  const u = json.users;
  if (!u) {
    console.log('invalid uname does not exist');
  }
  else if (u[0].pword !== user.pword) {
    console.log('invalid password');
  }
  else {
    console.log('valid uname and password')
    unameSetter (user.uname);
  }
})


  return (

    <BrowserRouter>

      <Routes>

        <Route path="/" element={<Layout />}>

          <Route index element={<Home  />} />

          <Route path="contacts" element={<Category  />} />

          <Route path="*" element={<NoPage />} />

        </Route>

      </Routes>

    </BrowserRouter>

  );

}  */
