import React, { useState, useRef } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Category from './Category.js';
import Home from './Home.js';
import Layout from './Layout.js';
import NoPage from './NoPage.js';
import Register from './Register.js';  // Import Register component
import './style.css';

export default function App() {
  const [uname, unameSetter] = useState(null);
  const unameRef = useRef();
  const pwordRef = useRef();

  const handleLogout = () => {
    unameSetter(null);
  }


  const handleLogin = async () => {
    const user = {
      uname: unameRef.current.value,
      pword: pwordRef.current.value,
    };
  
    const parameters = {
      method: "POST",
      headers: {
        "Accept": "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(user),
    };
  
    console.log("Attempting to log in with user:", user);
  
    const url = `http://localhost:5001/users/login`; // Assuming this is your login endpoint
  
    try {
      const res = await fetch(url, parameters);
  
      if (!res.ok) {
        throw new Error("Failed to fetch from the server");
      }
  
      const json = await res.json();
      console.log("Server response:", JSON.stringify(json));
  
      const u = json.users;
      if (!u) {
        console.log("Invalid username: does not exist");
      } else if (u[0].pword !== user.pword) {
        console.log("Invalid password");
      } else {
        console.log("Valid username and password");
        unameSetter(user.uname); // Set the username and log the user in
      }
    } catch (error) {
      console.error("Error:", error.message);
    }
  };
  
/*
