      },
      body: JSON.stringify(user)
    };

    console.log('Attempting to log in with user:', user);

    const url = `http://localhost:5001/users`;

    try {
      const res = await fetch(url, parameters);

      if (!res.ok) {
        throw new Error('Failed to fetch from the server');
      }

      const json = await res.json();
      console.log('Server response:', JSON.stringify(json));

      const u = json.users;
      if (!u) {
        console.log('Invalid username: does not exist');
      } else if (u[0].pword !== user.pword) {
        console.log('Invalid password');
      } else {
        console.log('Valid username and password');
        unameSetter(user.uname);
      }
    } catch (error) {
      console.error('Error:', error.message);
    }
  }
  */

  const handleRegister = async (user) => {
    const parameters = {
      method: "POST",
      headers: {
        "Accept": "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify(user)
    };

    console.log('Attempting to register with user:', user);

    const url = `http://localhost:5001/users/register`;  // Assuming this is your registration endpoint

    try {
      const res = await fetch(url, parameters);

      if (!res.ok) {
        throw new Error('Failed to fetch from the server');
      }

      const json = await res.json();
      console.log('Server response:', JSON.stringify(json));

      if (json.success) {
        console.log('Registration successful');
        unameSetter(user.uname); // Automatically log in the user after registration
      } else {
        console.log('Registration failed:', json.message);
      }
    } catch (error) {
      console.error('Error:', error.message);
    }
  }

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Layout />}>
          <Route index element={<Home uname={uname} />} />
          <Route path="contacts" element={<Category />} />
          <Route path="*" element={<NoPage />} />
          <Route path="register" element={<Register handleRegister={handleRegister} />} />  {/* Register route */}
        </Route>
      </Routes>

      {/* Login form */}
      <div>
        <input ref={unameRef} type="text" placeholder="Username" />
        <input ref={pwordRef} type="password" placeholder="Password" />
        <button onClick={handleLogin}>Login</button>
        <br />
        <button onClick={() => window.location.href = '/register'}>Register</button> {/* Link to register */}
      </div>
    </BrowserRouter>
  );
}




